insert  into `day_tours`(`day_tour_id`,`name`,`location`,`description`,`price`,`duration`,`rating`,`image_url`,`category`,`is_active`,`created_at`,`updated_at`,`region_id`,`departure_address`,`guide_fee`,`guide_id`,`cost_price`,`banner_image`,`pdf_url`,`pdf_name`,`pdf_enabled`,`product_showcase_image`) values (25,'霍巴特周边经典一日游','霍巴特','探索霍巴特周边的经典景点，包括历史悠久的里士满古镇、著名的萨拉曼卡集市、壮观的威灵顿山和卡斯卡德啤酒厂。这是一次完美的霍巴特周边文化与自然风光之旅，适合所有年龄段的游客。','100.00','8小时','5.0','http://hmlead22.oss-cn-beijing.aliyuncs.com/images/2025/06/13/8152669a-ad78-4dfa-ace8-5e89e8deea38.jpg?Expires=4903378517&OSSAccessKeyId=LTAI5tGP1rQXTQvbNSNRU5un&Signature=AauRa824KMOKgbGDvlyfoBKo7zs%3D','文化历史',1,'2025-06-13 11:05:02','2025-06-13 13:15:00',1,'霍巴特市中心指定接送点',NULL,NULL,NULL,'http://hmlead22.oss-cn-beijing.aliyuncs.com/images/2025/06/13/021ab0ae-b29a-4083-8f59-d8eed645dd92.jpg?Expires=4903384499&OSSAccessKeyId=LTAI5tGP1rQXTQvbNSNRU5un&Signature=5LJggtJSSI1%2Fiyerb7GMlpADEck%3D',NULL,NULL,0,NULL),(26,'布鲁尼岛美食生态一日游','布鲁尼岛','探索塔斯马尼亚著名的布鲁尼岛，体验渡轮上岛的独特旅程。品尝新鲜生蚝，参观蜂蜜农场，在冒险湾感受自然风光，寻找珍稀的白袋鼠。这是一次完美的美食与生态相结合的岛屿探险之旅。','110.00','8小时','5.0','http://hmlead22.oss-cn-beijing.aliyuncs.com/images/2025/06/13/3e99bf6e-cd2e-4597-9ef4-1ea57803c94c.jpg?Expires=4903379942&OSSAccessKeyId=LTAI5tGP1rQXTQvbNSNRU5un&Signature=ZQaH2kmKaoocTzGzcomGGEsae7c%3D','生态美食',1,'2025-06-13 11:50:13','2025-06-13 12:00:24',1,'霍巴特市中心指定接送点',NULL,NULL,NULL,'http://hmlead22.oss-cn-beijing.aliyuncs.com/images/2025/06/13/b75871ca-8968-4071-a55a-aaf09efe9f88.jpg?Expires=4903379714&OSSAccessKeyId=LTAI5tGP1rQXTQvbNSNRU5un&Signature=u2gvml9AkJ%2Bx6qe8QcmaiTCSHYA%3D',NULL,NULL,0,NULL),(27,'亚瑟港历史文化一日游（含门票）','亚瑟港','探索塔斯马尼亚著名的亚瑟港历史遗址，欣赏四大地质奇观的壮丽景色。上午游览棋盘道、塔斯曼拱门、恶魔厨房和喷水洞等自然奇观，下午深度参观亚瑟港监狱遗址，了解澳洲殖民历史。门票已包含，让您无忧畅游。','170.00','8小时','0.0','http://hmlead22.oss-cn-beijing.aliyuncs.com/images/2025/06/13/5b5e7637-75bf-4206-8d4e-be41a42eb4ba.jpg?Expires=4903381138&OSSAccessKeyId=LTAI5tGP1rQXTQvbNSNRU5un&Signature=M9V1zq%2BFuTyr9pfm7r%2FtP2fKY4g%3D','历史文化',1,'2025-06-13 12:13:32','2025-06-13 12:19:01',1,'霍巴特市中心指定接送点',NULL,NULL,NULL,'http://hmlead22.oss-cn-beijing.aliyuncs.com/images/2025/06/13/a7fc2f5a-b5b9-40be-8741-28d5d336c631.jpg?Expires=4903381097&OSSAccessKeyId=LTAI5tGP1rQXTQvbNSNRU5un&Signature=tWgl1QtUWX%2FYTCdk8vv18vXfK3o%3D',NULL,NULL,0,NULL),(28,'亚瑟港+1.5小时巡游一日游','亚瑟港历史遗址+塔斯曼半岛海域','探索塔斯马尼亚最著名的历史遗址亚瑟港，体验1.5小时海上巡游，从海上欣赏四大地质奇观，观赏海豹等海洋野生动物。包含亚瑟港门票和巡游船票，深度体验塔斯马尼亚的历史文化与自然美景。','288.00','9小时','0.0','http://hmlead22.oss-cn-beijing.aliyuncs.com/images/2025/06/13/de43d750-d6ae-41a4-8124-adee979379ca.jpg?Expires=4903382256&OSSAccessKeyId=LTAI5tGP1rQXTQvbNSNRU5un&Signature=bth5Y8DgF7qgxAr3%2FhVcbtueSeU%3D','历史文化',1,'2025-06-13 12:35:24','2025-06-13 12:42:10',NULL,'霍巴特市区酒店接送',NULL,NULL,NULL,'http://hmlead22.oss-cn-beijing.aliyuncs.com/images/2025/06/13/5cccbb6c-d4c2-4bf2-a8fc-8afbd5b50d82.jpg?Expires=4903382362&OSSAccessKeyId=LTAI5tGP1rQXTQvbNSNRU5un&Signature=uICzcT02N9Vh2LttzYU9AkSi1Mc%3D',NULL,NULL,0,NULL),(29,'酒杯湾自然风光一日游','Freycinet国家公园','探索塔斯马尼亚最美海湾酒杯湾，途经迷人海滨小镇Swansea，游览Coles Bay、蜜月湾和世界十大最美海滩之一的酒杯湾。徒步登顶观景台，俯瞰完美弧形海湾，体验纯净自然之美。','128.00','10小时','0.0','http://hmlead22.oss-cn-beijing.aliyuncs.com/images/2025/06/13/ca67710b-96fe-4d54-94b6-b020ca63f487.jpg?Expires=4903382962&OSSAccessKeyId=LTAI5tGP1rQXTQvbNSNRU5un&Signature=ZddsOJKPvdTGizzEXx3hzYIODcI%3D','自然风光',1,'2025-06-13 12:44:11','2025-06-13 13:04:38',NULL,'霍巴特市区酒店接送',NULL,NULL,NULL,'http://hmlead22.oss-cn-beijing.aliyuncs.com/images/2025/06/13/d26973d6-aa93-4161-915d-40383adb2dd8.jpg?Expires=4903383193&OSSAccessKeyId=LTAI5tGP1rQXTQvbNSNRU5un&Signature=U0IVOMN2ndYoLoJjqpAzGc%2FsOQk%3D',NULL,NULL,0,NULL),(30,'玛丽亚岛一日游（接送+代订船票）','玛丽亚岛国家公园','探索塔斯马尼亚独特的玛丽亚岛国家公园，观赏袋熊、袋鼠等野生动物，体验原始自然生态。我们提供霍巴特往返接送和代订船票服务，让您轻松前往这个野生动物天堂。注意：岛上无商店设施，需自备食物和饮水。','160.00','9小时','0.0','http://hmlead22.oss-cn-beijing.aliyuncs.com/images/2025/06/13/3b6c8710-a41a-44ad-8689-e4a10dcfdf04.jpg?Expires=4903383839&OSSAccessKeyId=LTAI5tGP1rQXTQvbNSNRU5un&Signature=aEiTRte0cF3DbZWOZoq9Pt5hatI%3D','自然风光',1,'2025-06-13 13:01:50','2025-06-13 14