/*Table structure for table `guide_availability` */

DROP TABLE IF EXISTS `guide_availability`;

CREATE TABLE `guide_availability` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '主键ID',
  `guide_id` int DEFAULT NULL,
  `date` date NOT NULL COMMENT '可用日期',
  `available_start_time` time NOT NULL COMMENT '开始时间',
  `available_end_time` time NOT NULL COMMENT '结束时间',
  `status` enum('available','busy','off','sick') DEFAULT 'available' COMMENT '状态',
  `max_groups` int DEFAULT '1' COMMENT '最大接团数',
  `notes` varchar(255) DEFAULT NULL COMMENT '备注',
  `created_time` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `updated_time` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '更新时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `idx_guide_date_time` (`guide_id`,`date`,`available_start_time`,`available_end_time`),
  KEY `idx_available_date` (`date`),
  KEY `idx_status` (`status`),
  CONSTRAINT `fk_guide_availability_guide` FOREIGN KEY (`guide_id`) REFERENCES `guides` (`guide_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=532 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='导游可用性表';

