/*Table structure for table `hotel_bookings` */

DROP TABLE IF EXISTS `hotel_bookings`;

CREATE TABLE `hotel_bookings` (
  `id` int NOT NULL AUTO_INCREMENT,
  `booking_reference` varchar(50) NOT NULL COMMENT '预订参考号(HB+日期+序号)',
  `tour_booking_id` int DEFAULT NULL COMMENT '关联旅游订单ID',
  `schedule_order_id` int DEFAULT NULL COMMENT '关联排团记录ID（核心关联）',
  `assignment_id` bigint DEFAULT NULL COMMENT '关联导游车辆分配ID',
  `hotel_id` int NOT NULL COMMENT '酒店ID',
  `room_type_id` int NOT NULL COMMENT '房型ID',
  `guest_name` varchar(100) NOT NULL COMMENT '入住人姓名（来自排团表contact_person）',
  `guest_phone` varchar(20) DEFAULT NULL COMMENT '联系电话（来自排团表contact_phone）',
  `guest_email` varchar(100) DEFAULT NULL COMMENT '联系邮箱',
  `check_in_date` date NOT NULL COMMENT '入住日期（来自排团表hotel_check_in_date）',
  `check_out_date` date NOT NULL COMMENT '退房日期（来自排团表hotel_check_out_date）',
  `nights` int GENERATED ALWAYS AS ((to_days(`check_out_date`) - to_days(`check_in_date`))) STORED COMMENT '住宿天数',
  `room_count` int DEFAULT '1' COMMENT '房间数量（来自排团表hotel_room_count）',
  `adult_count` int DEFAULT '1' COMMENT '成人数量（来自排团表adult_count）',
  `child_count` int DEFAULT '0' COMMENT '儿童数量（来自排团表child_count）',
  `total_guests` int GENERATED ALWAYS AS ((`adult_count` + `child_count`)) STORED COMMENT '总客人数',
  `room_rate` decimal(10,2) DEFAULT '0.00' COMMENT '房间单价(每晚)',
  `total_amount` decimal(10,2) GENERATED ALWAYS AS (((`room_rate` * `nights`) * `room_count`)) STORED COMMENT '总金额',
  `currency` varchar(3) DEFAULT 'AUD' COMMENT '货币代码',
  `special_requests` text COMMENT '特殊要求（来自排团表special_requests）',
  `booking_status` enum('pending','email_sent','confirmed','checked_in','checked_out','cancelled','no_show','rescheduled') DEFAULT 'pending',
  `payment_status` enum('unpaid','paid','refunded') DEFAULT 'unpaid' COMMENT '付款状态',
  `cancellation_policy` text COMMENT '取消政策',
  `booking_source` enum('agent','direct','system') DEFAULT 'system' COMMENT '预订来源',
  `booked_by` bigint DEFAULT NULL COMMENT '预订人ID',
  `notes` text COMMENT '内部备注',
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `email_sent_at` timestamp NULL DEFAULT NULL COMMENT '邮件发送时间',
  `email_sent_by` bigint DEFAULT NULL COMMENT '邮件发送人ID',
  `email_sent_to` varchar(255) DEFAULT NULL,
  `email_content` text,
  PRIMARY KEY (`id`),
  UNIQUE KEY `uk_booking_reference` (`booking_reference`),
  KEY `idx_tour_booking_id` (`tour_booking_id`),
  KEY `idx_schedule_order_id` (`schedule_order_id`),
  KEY `idx_assignment_id` (`assignment_id`),
  KEY `idx_hotel_id` (`hotel_id`),
  KEY `idx_check_in_date` (`check_in_date`),
  KEY `idx_booking_status` (`booking_status`),
  KEY `idx_guest_name` (`guest_name`),
  KEY `fk_hotel_booking_room_type` (`room_type_id`),
  CONSTRAINT `fk_hotel_booking_assignment` FOREIGN KEY (`assignment_id`) REFERENCES `tour_guide_vehicle_assignment` (`id`) ON DELETE SET NULL,
  CONSTRAINT `fk_hotel_booking_hotel` FOREIGN KEY (`hotel_id`) REFERENCES `hotels` (`id`),
  CONSTRAINT `fk_hotel_booking_room_type` FOREIGN KEY (`room_type_id`) REFERENCES `hotel_room_types` (`id`),
  CONSTRAINT `fk_hotel_booking_schedule` FOREIGN KEY (`schedule_order_id`) REFERENCES `tour_schedule_order` (`id`) ON DELETE SET NULL,
  CONSTRAINT `fk_hotel_booking_tour` FOREIGN KEY (`tour_booking_id`) REFERENCES `tour_bookings` (`booking_id`) ON DELETE SET NULL
) ENGINE=InnoDB AUTO_INCREMENT=39 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='酒店预订表（基于排团记录的业务模式）';

