/*Table structure for table `service_session` */

DROP TABLE IF EXISTS `service_session`;

CREATE TABLE `service_session` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '会话ID',
  `session_no` varchar(50) NOT NULL COMMENT '会话编号',
  `user_id` bigint NOT NULL COMMENT '用户ID',
  `user_type` tinyint NOT NULL DEFAULT '1' COMMENT '用户类型 1-普通用户(users) 2-代理商(agents) 3-代理商操作员(agent_operators)',
  `employee_id` bigint DEFAULT NULL COMMENT '分配的员工ID（客服）',
  `session_status` int NOT NULL DEFAULT '0' COMMENT '会话状态 0-等待分配 1-服务中 2-用户结束 3-客服结束 4-系统超时结束',
  `session_type` int NOT NULL DEFAULT '1' COMMENT '会话类型 1-主动咨询 2-AI转人工 3-投诉建议',
  `subject` varchar(200) DEFAULT NULL COMMENT '会话主题/问题描述',
  `user_rating` int DEFAULT NULL COMMENT '用户满意度评分 1-5分',
  `user_comment` text COMMENT '用户评价内容',
  `service_remark` text COMMENT '客服备注',
  `start_time` datetime NOT NULL COMMENT '会话开始时间',
  `end_time` datetime DEFAULT NULL COMMENT '会话结束时间',
  `wait_duration` int DEFAULT NULL COMMENT '等待时长（秒）',
  `service_duration` int DEFAULT NULL COMMENT '服务时长（秒）',
  `create_time` datetime NOT NULL COMMENT '创建时间',
  `update_time` datetime NOT NULL COMMENT '更新时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `uk_session_no` (`session_no`),
  KEY `idx_user_id` (`user_id`),
  KEY `idx_service_id` (`employee_id`),
  KEY `idx_session_status` (`session_status`),
  KEY `idx_create_time` (`create_time`),
  KEY `idx_employee_id` (`employee_id`),
  KEY `idx_user_type_id` (`user_type`,`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=48 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='服务会话表';

