/*Table structure for table `system_notification` */

DROP TABLE IF EXISTS `system_notification`;

CREATE TABLE `system_notification` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '通知ID',
  `type` int NOT NULL DEFAULT '1' COMMENT '通知类型 1-新订单 2-聊天请求 3-订单修改 4-用户注册 5-退款申请 6-投诉建议 7-系统消息',
  `title` varchar(200) NOT NULL COMMENT '通知标题',
  `content` text NOT NULL COMMENT '通知内容',
  `icon` varchar(50) DEFAULT NULL COMMENT '通知图标',
  `related_id` bigint DEFAULT NULL COMMENT '相关数据ID',
  `related_type` varchar(50) DEFAULT NULL COMMENT '相关数据类型',
  `level` int NOT NULL DEFAULT '1' COMMENT '通知级别 1-普通 2-重要 3-紧急',
  `is_read` int NOT NULL DEFAULT '0' COMMENT '是否已读 0-未读 1-已读',
  `receiver_role` int NOT NULL DEFAULT '1' COMMENT '接收者角色 1-管理员 2-客服 3-特定用户',
  `receiver_id` bigint DEFAULT NULL COMMENT '特定接收者ID',
  `create_time` datetime NOT NULL COMMENT '创建时间',
  `read_time` datetime DEFAULT NULL COMMENT '已读时间',
  `expire_time` datetime DEFAULT NULL COMMENT '过期时间',
  PRIMARY KEY (`id`),
  KEY `idx_receiver_role` (`receiver_role`),
  KEY `idx_receiver_id` (`receiver_id`),
  KEY `idx_is_read` (`is_read`),
  KEY `idx_type` (`type`),
  KEY `idx_create_time` (`create_time`),
  KEY `idx_expire_time` (`expire_time`)
) ENGINE=InnoDB AUTO_INCREMENT=545 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='系统通知表';

