/*Table structure for table `tour_bookings` */

DROP TABLE IF EXISTS `tour_bookings`;

CREATE TABLE `tour_bookings` (
  `booking_id` int NOT NULL AUTO_INCREMENT,
  `order_number` varchar(50) NOT NULL COMMENT '订单号',
  `tour_id` int NOT NULL,
  `tour_type` enum('day_tour','group_tour') NOT NULL,
  `user_id` int DEFAULT NULL,
  `agent_id` int DEFAULT NULL,
  `booking_date` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `flight_number` varchar(100) DEFAULT NULL COMMENT '航班号',
  `arrival_departure_time` datetime DEFAULT NULL COMMENT '到达航班起飞时间',
  `arrival_landing_time` datetime DEFAULT NULL COMMENT '到达航班降落时间',
  `return_flight_number` varchar(100) DEFAULT NULL COMMENT '返程航班号',
  `departure_departure_time` datetime DEFAULT NULL COMMENT '返程航班起飞时间',
  `departure_landing_time` datetime DEFAULT NULL COMMENT '返程航班降落时间',
  `tour_start_date` date NOT NULL COMMENT '行程开始日期',
  `tour_end_date` date NOT NULL COMMENT '行程结束日期',
  `pickup_date` date DEFAULT NULL COMMENT '接客日期',
  `dropoff_date` date DEFAULT NULL COMMENT '送客日期',
  `pickup_location` varchar(255) DEFAULT NULL COMMENT '接客地点',
  `dropoff_location` varchar(255) DEFAULT NULL COMMENT '送客地点',
  `service_type` varchar(50) DEFAULT NULL,
  `group_size` int DEFAULT '1',
  `adult_count` int DEFAULT '0' COMMENT '成人数量',
  `child_count` int DEFAULT '0' COMMENT '儿童数量',
  `luggage_count` int DEFAULT '0',
  `passenger_contact` varchar(50) DEFAULT NULL,
  `contact_person` varchar(100) DEFAULT NULL,
  `contact_phone` varchar(50) DEFAULT NULL,
  `hotel_level` varchar(50) DEFAULT NULL,
  `room_type` varchar(50) DEFAULT NULL,
  `hotel_room_count` int DEFAULT '1' COMMENT '酒店房间数量',
  `hotel_check_in_date` date DEFAULT NULL COMMENT '酒店入住日期',
  `hotel_check_out_date` date DEFAULT NULL COMMENT '酒店退房日期',
  `room_details` varchar(255) DEFAULT NULL COMMENT '每个房间的样式',
  `special_requests` text,
  `itinerary_details` text,
  `status` enum('pending','confirmed','cancelled','completed') DEFAULT 'pending',
  `payment_status` enum('unpaid','partial','paid') DEFAULT 'unpaid',
  `total_price` decimal(10,2) NOT NULL,
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `is_first_order` tinyint(1) DEFAULT '0' COMMENT '是否为用户首单',
  `from_referral` tinyint(1) DEFAULT '0' COMMENT '是否来自推荐',
  `referral_code` varchar(20) DEFAULT NULL COMMENT '使用的推荐码',
  `selected_optional_tours` text COMMENT '选择的可选项目（JSON格式存储）',
  `operator_id` int DEFAULT NULL COMMENT '操作员ID',
  `user_hidden` tinyint(1) DEFAULT '0' COMMENT '??????????????',
  `user_hidden_at` datetime DEFAULT NULL COMMENT '??????',
  PRIMARY KEY (`booking_id`),
  UNIQUE KEY `idx_order_number` (`order_number`),
  KEY `idx_user_id` (`user_id`),
  KEY `idx_agent_id` (`agent_id`),
  KEY `idx_tour_id` (`tour_id`),
  KEY `idx_operator_id` (`operator_id`)
) ENGINE=InnoDB AUTO_INCREMENT=340 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;

