/*Table structure for table `tour_schedule_order` */

DROP TABLE IF EXISTS `tour_schedule_order`;

CREATE TABLE `tour_schedule_order` (
  `id` int NOT NULL AUTO_INCREMENT,
  `booking_id` int NOT NULL COMMENT '订单ID',
  `day_number` int NOT NULL COMMENT '第几天（固定顺序）',
  `tour_id` int DEFAULT NULL COMMENT '旅游项目ID (额外行程时可为空)',
  `tour_type` enum('day_tour','group_tour') NOT NULL COMMENT '产品类型',
  `tour_date` date NOT NULL COMMENT '实际行程日期',
  `title` varchar(100) DEFAULT NULL COMMENT '行程标题',
  `description` text COMMENT '行程描述',
  `display_order` int DEFAULT '0' COMMENT '显示顺序',
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  `updated_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `order_number` varchar(50) DEFAULT NULL COMMENT '订单号',
  `adult_count` int DEFAULT '0' COMMENT '成人人数',
  `child_count` int DEFAULT '0' COMMENT '儿童人数',
  `contact_person` varchar(100) DEFAULT NULL COMMENT '联系人姓名',
  `contact_phone` varchar(50) DEFAULT NULL COMMENT '联系电话',
  `pickup_location` varchar(255) DEFAULT NULL COMMENT '接送地点',
  `dropoff_location` varchar(255) DEFAULT NULL COMMENT '送达地点',
  `special_requests` text COMMENT '特殊要求/备注',
  `guide_remarks` text COMMENT '导游专用备注',
  `selected_optional_tours` text,
  `luggage_count` int DEFAULT '0' COMMENT '行李数量',
  `hotel_level` varchar(50) DEFAULT NULL COMMENT '酒店等级',
  `room_type` varchar(50) DEFAULT NULL COMMENT '房间类型',
  `hotel_room_count` int DEFAULT '1' COMMENT '酒店房间数量',
  `hotel_check_in_date` date DEFAULT NULL COMMENT '酒店入住日期',
  `hotel_check_out_date` date DEFAULT NULL COMMENT '酒店退房日期',
  `room_details` varchar(255) DEFAULT NULL COMMENT '房间详情',
  `flight_number` varchar(100) DEFAULT NULL COMMENT '到达航班号',
  `arrival_departure_time` datetime DEFAULT NULL COMMENT '到达航班起飞时间',
  `arrival_landing_time` datetime DEFAULT NULL COMMENT '到达航班降落时间',
  `return_flight_number` varchar(100) DEFAULT NULL COMMENT '返程航班号',
  `departure_departure_time` datetime DEFAULT NULL COMMENT '返程航班起飞时间',
  `departure_landing_time` datetime DEFAULT NULL COMMENT '返程航班降落时间',
  `tour_start_date` date DEFAULT NULL COMMENT '行程开始日期',
  `tour_end_date` date DEFAULT NULL COMMENT '行程结束日期',
  `pickup_date` date DEFAULT NULL COMMENT '接客日期',
  `dropoff_date` date DEFAULT NULL COMMENT '送客日期',
  `passenger_contact` varchar(50) DEFAULT NULL COMMENT '乘客联系方式',
  `itinerary_details` text COMMENT '行程详情',
  `is_first_order` tinyint(1) DEFAULT '0' COMMENT '是否首单',
  `from_referral` tinyint(1) DEFAULT '0' COMMENT '是否来自推荐',
  `referral_code` varchar(20) DEFAULT NULL COMMENT '推荐码',
  `service_type` varchar(50) DEFAULT NULL COMMENT '服务类型',
  `payment_status` varchar(20) DEFAULT NULL COMMENT '支付状态',
  `total_price` decimal(10,2) DEFAULT NULL COMMENT '订单总价',
  `user_id` int DEFAULT NULL COMMENT '用户ID',
  `agent_id` int DEFAULT NULL COMMENT '代理商ID',
  `operator_id` int DEFAULT NULL COMMENT '操作员ID',
  `booking_date` timestamp NULL DEFAULT NULL COMMENT '预订日期',
  `group_size` int DEFAULT NULL COMMENT '团队大小',
  `status` varchar(20) DEFAULT NULL COMMENT '订单状态',
  `tour_name` varchar(255) DEFAULT NULL COMMENT '旅游产品名称',
  `tour_location` varchar(255) DEFAULT NULL COMMENT '旅游目的地',
  `is_extra_schedule` tinyint(1) DEFAULT '0' COMMENT 'Whether it is an extra schedule (0: normal, 1: extra)',
  `schedule_type` varchar(20) DEFAULT 'normal' COMMENT 'Schedule type (pickup: pickup, dropoff: dropoff, extra_day: extra day tour, normal: normal)',
  `pickup_time` varchar(10) DEFAULT NULL COMMENT 'Pickup time (HH:mm format)',
  `dropoff_time` varchar(10) DEFAULT NULL COMMENT 'Dropoff time (HH:mm format)',
  `version` int NOT NULL DEFAULT '1' COMMENT '版本号，用于乐观锁控制',
  `last_modified_by` varchar(100) DEFAULT NULL COMMENT '最后修改人',
  `last_modified_time` datetime DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '最后修改时间',
  PRIMARY KEY (`id`),
  UNIQUE KEY `idx_booking_day` (`booking_id`,`day_number`),
  KEY `idx_booking_date` (`booking_id`,`tour_date`),
  KEY `idx_booking_id` (`booking_id`),
  KEY `idx_tour_date` (`tour_date`),
  KEY `idx_agent_id` (`agent_id`),
  KEY `idx_user_id` (`user_id`),
  KEY `idx_status` (`status`),
  KEY `idx_booking_day_date` (`booking_id`,`day_number`,`tour_date`),
  KEY `idx_flight_number` (`flight_number`),
  KEY `idx_operator_id` (`operator_id`),
  KEY `idx_tour_start_date` (`tour_start_date`),
  KEY `idx_tour_schedule_extra` (`is_extra_schedule`,`schedule_type`),
  KEY `idx_tour_schedule_date_type` (`tour_date`,`schedule_type`),
  KEY `idx_tour_schedule_guide_remarks` (`guide_remarks`(100)),
  KEY `idx_tour_schedule_version` (`id`,`version`)
