/*!50003 CREATE */ /*!50017 DEFINER = 'root'@'localhost' */ /*!50003 TRIGGER `trg_hotel_booking_reference` BEFORE INSERT ON `hotel_bookings` FOR EACH ROW BEGIN
    DECLARE next_id INT;
