        SELECT AUTO_INCREMENT INTO next_id 
        FROM information_schema.TABLES 
        WHERE TABLE_SCHEMA = DATABASE() AND TABLE_NAME = 'hotel_bookings';
        
        -- 生成格式：HB + YYYYMMDD + 4位序号
