/* Procedure structure for procedure `AutoAssignCustomerService` */

/*!50003 DROP PROCEDURE IF EXISTS  `AutoAssignCustomerService` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `AutoAssignCustomerService`(
    IN p_session_id BIGINT,
    IN p_skill_tag VARCHAR(50)
)
BEGIN
    DECLARE v_employee_id BIGINT DEFAULT NULL;
    
    -- 查找可用的客服（在线且未满负荷）
    SELECT id INTO v_employee_id
    FROM employees 
    WHERE role = 3 
        AND online_status = 1 
        AND current_customer_count < max_concurrent_customers
        AND (p_skill_tag IS NULL OR FIND_IN_SET(p_skill_tag, skill_tags) > 0)
    ORDER BY current_customer_count ASC, service_level DESC
    LIMIT 1;
    
    -- 如果找到可用客服，则分配
    IF v_employee_id IS NOT NULL THEN
        UPDATE service_session 
        SET employee_id = v_employee_id, 
            session_status = 1,
            assign_time = NOW(),
            update_time = NOW()
        WHERE id = p_session_id;
        
        UPDATE employees 
        SET current_customer_count = current_customer_count + 1,
            update_time = NOW()
        WHERE id = v_employee_id;
    END IF;
END */$$
DELIMITER ;

/* Procedure structure for procedure `sp_auto_assign_guide_vehicle` */

/*!50003 DROP PROCEDURE IF EXISTS  `sp_auto_assign_guide_vehicle` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_auto_assign_guide_vehicle`(
    IN p_assignment_date DATE,
    IN p_location VARCHAR(100),
    IN p_total_people INT,
    IN p_start_time TIME,
    IN p_end_time TIME,
    OUT p_assignment_id BIGINT,
    OUT p_result_message VARCHAR(255)
)
BEGIN
    DECLARE v_guide_id BIGINT DEFAULT NULL;
    DECLARE v_vehicle_id BIGINT DEFAULT NULL;
    DECLARE v_error_msg VARCHAR(255) DEFAULT '';
    
    DECLARE EXIT HANDLER FOR SQLEXCEPTION
    BEGIN
        ROLLBACK;
        SET p_result_message = CONCAT('错误: ', v_error_msg);
        SET p_assignment_id = NULL;
    END;
    
    START TRANSACTION;
    
    -- 查找可用的导游
    SELECT g.guide_id INTO v_guide_id
    FROM guides g
    LEFT JOIN guide_availability ga ON g.guide_id = ga.guide_id 
        AND ga.available_date = p_assignment_date
        AND ga.start_time <= p_start_time 
        AND ga.end_time >= p_end_time
        AND ga.status = 'available'
    WHERE g.is_active = 1
    AND NOT EXISTS (
        SELECT 1 FROM guide_daily_assignment gda 
        WHERE gda.guide_id = g.guide_id 
        AND gda.assignment_date = p_assignment_date
        AND gda.assignment_status IN ('confirmed', 'in_progress')
        AND ((gda.start_time <= p_start_time AND gda.end_time > p_start_time)
             OR (gda.start_time < p_end_time AND gda.end_time >= p_end_time))
    )
    ORDER BY g.experience_years DESC
    LIMIT 1;
    
    -- 查找可用的车辆
    SELECT v.vehicle_id INTO v_vehicle_id
    FROM vehicles v
    LEFT JOIN vehicle_availability va ON v.vehicle_id = va.vehicle_id
        AND va.available_date = p_assignment_date
        AND va.start_time <= p_start_time
        AND va.end_time >= p_end_time
        AND va.status = 'available'
    WHERE v.status = 1
    AND v.seat_count >= p_total_people
    AND NOT EXISTS (
        SELECT 1 FROM guide_daily_assignment gda
        WHERE gda.vehicle_id = v.vehicle_id
        AND gda.assignment_date = p_assignment_date
        AND gda.assignment_status IN ('confirmed', 'in_progress')
        AND ((gda.start_time <= p_start_time AND gda.end_time > p_start_time)
             OR (gda.start_time < p_end_time AND gda.end_time >= p_end_time))
    )
    ORDER BY v.seat_count ASC
    LIMIT 1;
    
    -- 创建分配记录
    IF v_guide_id IS NOT NULL AND v_vehicle_id IS NOT NULL THEN
        INSERT INTO guide_daily_assignment (
            assignment_date, location, guide_id, vehicle_id, 
            total_people, start_time, end_time, assignment_status
        ) VALUES (
            p_assignment_date, p_location, v_guide_id, v_vehicle_id,
            p_total_people, p_start_time, p_end_time, 'confirmed'
        );
        
        SET p_assignment_id = LAST_INSERT_ID();
        SET p_result_message = '自动分配成功';
        
    ELSE
        SET p_result_message = CONCAT('分配失败: ',
            CASE 
                WHEN v_guide_id IS NULL AND v_vehicle_id IS NULL THEN '没有可用的导游和车辆'
                WHEN v_guide_id IS NULL THEN '没有可用的导游'
                WHEN v_vehicle_id IS NULL THEN '没有可用的车辆'
            END
        );
        SET p_assignment_id = NULL;
    END IF;
    
    COMMIT;
END */$$
DELIMITER ;

