mapper.select:
select * from agents


        and company_name like concat('%', #{companyName}, '%')


        and contact_person like concat('%', #{contactPerson}, '%')


        and phone = #{phone}


        and status = #{status}

mapper.insert:
insert into agents(
    username, password, company_name, contact_person, 
    phone, email, avatar, use_avatar_as_logo, discount_rate, discount_level_id, status
) values(
    #{username}, #{password}, #{companyName}, #{contactPerson}, 
    #{phone}, #{email}, #{avatar}, #{useAvatarAsLogo}, #{discountRate}, #{discountLevelId}, #{status}
)

mapper.update:
update agents
username = #{username}, company_name = #{companyName}, contact_person = #{contactPerson}, phone = #{phone}, email = #{email}, avatar = #{avatar}, use_avatar_as_logo = #{useAvatarAsLogo}, discount_rate = #{discountRate}, discount_level_id = #{discountLevelId}, status = #{status}, password = #{password},
where id = #{id}

mapper.select:
SELECT COUNT(*) 
FROM tour_bookings
WHERE agent_id = #{agentId}
