mapper.insert:
INSERT INTO attractions (
    attraction_name, attraction_name_en, location, contact_phone, contact_person, 
    contact_email, booking_type, website_url, email_address, advance_days, 
    description, images, rating, status, created_at, updated_at
) VALUES (
    #{attractionName}, #{attractionNameEn}, #{location}, #{contactPhone}, #{contactPerson}, 
    #{contactEmail}, #{bookingType}, #{websiteUrl}, #{emailAddress}, #{advanceDays}, 
    #{description}, #{images}, #{rating}, #{status}, #{createdAt}, #{updatedAt}
)

mapper.update:
UPDATE attractions 
SET status = #{status}, updated_at = NOW()
WHERE id = #{id}

mapper.select:
SELECT FROM attractions
AND (attraction_name LIKE CONCAT('%', #{attractionName}, '%') OR attraction_name_en LIKE CONCAT('%', #{attractionName}, '%'))
AND location LIKE CONCAT('%', #{location}, '%')
AND booking_type = #{bookingType}
AND status = #{status}
ORDER BY created_at DESC
