mapper.select:
SELECT 
    dt.day_tour_id as id, dt.day_tour_id as dayTourId, dt.name, dt.description, dt.price, dt.duration, dt.location, 
    dt.departure_address, dt.guide_fee, dt.rating,  
    dt.region_id, dt.category, dt.image_url as coverImage, dt.banner_image as bannerImage, r.name as regionName,
    dt.is_active as isActive, dt.show_on_user_site as showOnUserSite, dt.small_group_price_difference as smallGroupPriceDifference
FROM day_tours dt
LEFT JOIN regions r ON dt.region_id = r.region_id


        AND dt.name LIKE CONCAT('%', #{name}, '%')


        AND dt.location = #{location}


        AND dt.region_id = #{regionId}


        AND dt.price >= #{minPrice}


        AND dt.price <= #{maxPrice}


        AND dt.day_tour_id IN (
            SELECT dtt.day_tour_id FROM day_tour_theme_relation dtt
            JOIN day_tour_themes t ON dtt.theme_id = t.theme_id
            WHERE t.name = #{category}
        )


        AND dt.show_on_user_site = #{showOnUserSite}




        ORDER BY ${orderBy}


        ORDER BY dt.day_tour_id DESC

mapper.update:
UPDATE day_tours
name = #{name}, location = #{location}, short_description = #{shortDescription}, overview_image = #{overviewImage}, description = #{description}, price = #{price}, duration = #{duration}, image_url = #{imageUrl}, banner_image = #{bannerImage}, product_showcase_image = #{productShowcaseImage}, category = #{category}, is_active = #{isActive}, updated_at = #{updatedAt}, region_id = #{regionId}, departure_address = #{departureAddress}, guide_fee = #{guideFee}, guide_id = #{guideId}, cost_price = #{costPrice}, small_group_price_difference = #{smallGroupPriceDifference},
WHERE day_tour_id = #{dayTourId}
