mapper.select:
SELECT e.id, e.username, e.name, e.password, e.phone, e.email, e.avatar, e.sex, e.id_number as idNumber, e.role, e.work_status as workStatus, e.create_time as createTime, e.update_time as updateTime, e.create_user as createUser, e.update_user as updateUser, e.service_no as serviceNo, e.online_status as onlineStatus, e.max_concurrent_customers as maxConcurrentCustomers, e.current_customer_count as currentCustomerCount, e.skill_tags as skillTags, e.service_level as serviceLevel, e.last_login_time as lastLoginTime, e.last_active_time as lastActiveTime, e.is_guide as isGuide, e.guide_level as guideLevel, e.department, e.status as status, e.operator_type as operatorType, e.is_tour_master as isTourMaster, e.can_assign_orders as canAssignOrders, -- ÈÇÆÁÆ±ÈÖçÁΩÆÂ≠óÊÆµ e.email_password as emailPassword, e.email_host as emailHost, e.email_port as emailPort, e.email_enabled as emailEnabled, e.email_ssl_enabled as emailSslEnabled, e.email_auth_enabled as emailAuthEnabled FROM employees e
AND e.status = #{status}
AND e.name LIKE CONCAT('%', #{name}, '%')
AND e.role LIKE CONCAT('%', #{role}, '%')
AND e.work_status = #{workStatus}
ORDER BY e.id DESC

SELECT e.id, e.username, e.name, e.password, e.phone, e.email, e.avatar, e.sex, e.id_number as idNumber, e.role, e.work_status as workStatus, e.create_time as createTime, e.update_time as updateTime, e.create_user as createUser, e.update_user as updateUser, e.service_no as serviceNo, e.online_status as onlineStatus, e.max_concurrent_customers as maxConcurrentCustomers, e.current_customer_count as currentCustomerCount, e.skill_tags as skillTags, e.service_level as serviceLevel, e.last_login_time as lastLoginTime, e.last_active_time as lastActiveTime, e.is_guide as isGuide, e.guide_level as guideLevel, e.department, e.status as status, e.operator_type as operatorType, e.is_tour_master as isTourMaster, e.can_assign_orders as canAssignOrders, e.dept_id as deptId, e.position_id as positionId, -- ÈÇÆÁÆ±ÈÖçÁΩÆÂ≠óÊÆµ e.email_password as emailPassword, e.email_host as emailHost, e.email_port as emailPort, e.email_enabled as emailEnabled, e.email_ssl_enabled as emailSslEnabled, e.email_auth_enabled as emailAuthEnabled FROM employees e
AND e.status = #{status}
AND e.dept_id = #{deptId}
AND e.name LIKE CONCAT('%', #{name}, '%')
AND e.role LIKE CONCAT('%', #{role}, '%')
AND e.work_status = #{workStatus}
ORDER BY e.id DESC

mapper.update:
UPDATE employees
name = #{name}, username = #{username}, password = #{password}, phone = #{phone}, email = #{email}, avatar = #{avatar}, sex = #{sex}, id_number = #{idNumber}, role = #{role}, dept_id = #{deptId}, position_id = #{positionId}, status = #{status}, work_status = #{workStatus}, last_login_time = #{lastLoginTime},
operator_type = #{operatorType}, is_tour_master = #{isTourMaster}, can_assign_orders = #{canAssignOrders},
email_password = #{emailPassword}, email_host = #{emailHost}, email_port = #{emailPort}, email_enabled = #{emailEnabled}, email_ssl_enabled = #{emailSslEnabled}, email_auth_enabled = #{emailAuthEnabled}, update_time = #{updateTime}, update_user = #{updateUser}
WHERE id = #{id}

mapper.select:
SELECT e.id as employeeId, e.username, e.name, e.phone, e.sex, e.service_no as serviceNo, e.online_status as onlineStatus, e.max_concurrent_customers as maxConcurrentCustomers, e.current_customer_count as currentCustomerCount, e.skill_tags as skillTags, e.service_level as serviceLevel, e.last_login_time as lastLoginTime, e.last_active_time as lastActiveTime, e.create_time as createTime, e.update_time as updateTime, COALESCE(session_stats.total_sessions, 0) as totalSessions, COALESCE(session_stats.active_sessions, 0) as activeSessions, COALESCE(session_stats.avg_rating, 0) as avgRating, COALESCE(session_stats.total_service_duration, 0) as totalServiceDuration, COALESCE(session_stats.avg_service_duration, 0) as avgServiceDuration FROM employees e LEFT JOIN ( SELECT employee_id, COUNT(*) as total_sessions, COUNT(CASE WHEN session_status IN (0,1) THEN 1 END) as active_sessions, AVG(user_rating) as avg_rating, SUM(service_duration) as total_service_duration, AVG(service_duration) as avg_service_duration FROM service_session WHERE employee_id IS NOT NULL GROUP BY employee_id ) session_stats ON e.id = session_stats.employee_id WHERE (e.role LIKE '%Service%' OR e.role LIKE '%ÂÆ¢Êúç%' OR e.role LIKE '%Manager%' OR e.role LIKE '%ÁÆ°ÁêÜ%')
AND e.name LIKE CONCAT('%', #{name}, '%')
AND e.online_status = #{onlineStatus}
AND e.service_level = #{serviceLevel}
ORDER BY e.role ASC, e.service_level DESC, e.create_time DESC

mapper.update:
UPDATE employees
name = #{name}, username = #{username}, password = #{password}, phone = #{phone}, sex = #{sex}, service_no = #{serviceNo}, online_status = #{onlineStatus}, max_concurrent_customers = #{maxConcurrentCustomers}, current_customer_count = #{currentCustomerCount}, skill_tags = #{skillTags}, service_level = #{serviceLevel}, work_status = #{workStatus}, last_login_time = #{lastLoginTime}, last_active_time = #{lastActiveTime}, update_time = #{updateTime}
WHERE id = #{id} AND (role LIKE '%Service%' OR role LIKE '%ÂÆ¢Êúç%' OR role LIKE '%Manager%' OR role LIKE '%ÁÆ°ÁêÜ%')
