mapper.select:
SELECT * FROM group_tour_daily_prices
WHERE group_tour_id = #{groupTourId}

    AND price_date >= #{startDate}


    AND price_date <= #{endDate}

ORDER BY price_date

SELECT * FROM group_tour_daily_prices
WHERE 1=1

    AND price_date >= #{startDate}


    AND price_date <= #{endDate}

ORDER BY group_tour_id, price_date

SELECT * FROM group_tour_daily_prices
WHERE group_tour_id = #{groupTourId}
AND price_date = #{priceDate}
LIMIT 1

mapper.delete:
DELETE FROM group_tour_daily_prices
WHERE group_tour_id = #{groupTourId}
AND price_date = #{priceDate}
