mapper.select:
SELECT gt.group_tour_id AS id, gt.title AS name, gt.short_title AS shortDescription, gt.description, gt.price, gt.discounted_price AS discountedPrice, gt.duration, gt.location, gt.rating, gt.image_url AS coverImage, gt.banner_image AS bannerImage, gt.departure_address AS departureAddress, gt.guide_id AS guideId, gt.is_active AS isActive, gt.show_on_user_site AS showOnUserSite, gt.small_group_price_difference AS smallGroupPriceDifference FROM group_tours gt
AND gt.title LIKE CONCAT('%', #{title}, '%')
AND gt.location LIKE CONCAT('%', #{location}, '%')
AND EXISTS ( SELECT 1 FROM group_tour_theme_relation gtr JOIN group_tour_themes gtt ON gtr.theme_id = gtt.theme_id WHERE gtr.group_tour_id = gt.group_tour_id AND gtt.name = #{category} )
AND gt.price >= #{minPrice}
AND gt.price <= #{maxPrice}
AND gt.days >= #{minDays}
AND gt.days <= #{maxDays}
AND gt.show_on_user_site = #{showOnUserSite}
ORDER BY gt.group_tour_id DESC

SELECT ad.date_id AS id, ad.start_date AS startDate, ad.end_date AS endDate, ad.price, ad.available_slots AS availableSlots FROM available_dates ad WHERE ad.group_tour_id = #{tourId}
AND ad.start_date >= #{startDate}
AND ad.start_date <= #{endDate}
ORDER BY ad.start_date

mapper.update:
UPDATE group_tours
title = #{name}, short_description = #{shortDescription}, overview_image = #{overviewImage}, description = #{description}, price = #{price}, duration = #{duration}, location = #{location}, start_date = #{startDate}, end_date = #{endDate}, min_people = #{minPeople}, max_people = #{maxPeople}, image_url = #{coverImage}, banner_image = #{bannerImage}, product_showcase_image = #{productShowcaseImage}, region_id = #{regionId}, departure_address = #{departureAddress}, guide_id = #{guideId}, rating = #{rating}, discounted_price = #{discountedPrice}, small_group_price_difference = #{smallGroupPriceDifference}, updated_at = NOW()
WHERE group_tour_id = #{id}
