mapper.insert:
INSERT INTO hotel_room_types (
    hotel_id, room_type, room_level, room_type_code, max_occupancy, bed_type, 
    room_size, base_price, room_facilities, status, created_at, updated_at
) VALUES (
    #{hotelId}, #{roomType}, #{roomLevel}, #{roomTypeCode}, #{maxOccupancy}, #{bedType}, 
    #{roomSize}, #{basePrice}, #{amenities}, #{status}, #{createdAt}, #{updatedAt}
)

mapper.select:
SELECT COUNT(*) 
FROM hotel_bookings 
WHERE room_type_id = #{id}
