mapper.select:



    AND (tb.user_hidden IS NULL OR tb.user_hidden = false)


        AND tb.user_id = #{userId}


        AND tb.agent_id = #{agentId}


        AND tb.operator_id = #{operatorId}


        AND tb.order_number LIKE CONCAT('%', #{orderNumber}, '%')


        AND tb.status = #{status}


        AND tb.payment_status = #{paymentStatus}


        AND tb.tour_type = #{tourType}


        AND tb.tour_start_date >= #{startDate}


        AND tb.tour_end_date <= #{endDate}


        AND tb.contact_person LIKE CONCAT('%', #{contactPerson}, '%')


        AND tb.contact_phone LIKE CONCAT('%', #{contactPhone}, '%')


ORDER BY tb.created_at DESC

mapper.update:
UPDATE tour_bookings
order_number = #{orderNumber}, tour_id = #{tourId}, tour_type = #{tourType}, user_id = #{userId}, agent_id = #{agentId}, operator_id = #{operatorId}, booking_date = #{bookingDate}, flight_number = #{flightNumber}, return_flight_number = #{returnFlightNumber}, tour_start_date = #{tourStartDate}, tour_end_date = #{tourEndDate}, pickup_date = #{pickupDate}, dropoff_date = #{dropoffDate}, pickup_location = #{pickupLocation}, dropoff_location = #{dropoffLocation}, service_type = #{serviceType}, group_size = #{groupSize}, adult_count = #{adultCount}, child_count = #{childCount}, luggage_count = #{luggageCount}, passenger_contact = #{passengerContact}, contact_person = #{contactPerson}, contact_phone = #{contactPhone}, hotel_level = #{hotelLevel}, room_type = #{roomType}, hotel_room_count = #{hotelRoomCount}, room_details = #{roomDetails}, special_requests = #{specialRequests}, itinerary_details = #{itineraryDetails}, status = #{status}, payment_status = #{paymentStatus}, total_price = #{totalPrice}, updated_at = NOW()
WHERE booking_id = #{bookingId}

UPDATE tour_bookings
SET status = #{status}, updated_at = NOW()
WHERE booking_id = #{bookingId}

UPDATE tour_bookings
SET payment_status = #{paymentStatus}, updated_at = NOW()
WHERE booking_id = #{bookingId}
