mapper.select:
select p.* from passengers p
join booking_passenger_relation r on p.passenger_id = r.passenger_id
where r.booking_id = #{bookingId}

mapper.insert:
insert into passengers(
    full_name, gender, date_of_birth, is_child, child_age, passport_number, passport_expiry,
    nationality, phone, wechat_id, email, emergency_contact_name, emergency_contact_phone,
    dietary_requirements, medical_conditions, luggage_count, special_requests,
    created_at, updated_at
) values(
    #{fullName}, 

        #{gender}
        null
    , 

        #{dateOfBirth}
        null
    , 
    #{isChild}, 
    #{childAge}, 

        #{passportNumber}
        null
    , 

        #{passportExpiry}
        null
    ,

        #{nationality}
        null
    , 
    #{phone}, 
    #{wechatId}, 

        #{email}
        null
    , 

        #{emergencyContactName}
        null
    , 

        #{emergencyContactPhone}
        null
    ,

        #{dietaryRequirements}
        null
    , 

        #{medicalConditions}
        null
    , 

        #{luggageCount}
        0
    , 

        #{specialRequests}
        null
    ,

        #{createdAt}
        now()
    , 

        #{updatedAt}
        now()

)

mapper.update:
update passengers
full_name = #{fullName}, gender = #{gender}, date_of_birth = #{dateOfBirth}, is_child = #{isChild}, child_age = #{childAge}, passport_number = #{passportNumber}, passport_expiry = #{passportExpiry}, nationality = #{nationality}, phone = #{phone}, wechat_id = #{wechatId}, email = #{email}, emergency_contact_name = #{emergencyContactName}, emergency_contact_phone = #{emergencyContactPhone}, dietary_requirements = #{dietaryRequirements}, medical_conditions = #{medicalConditions}, luggage_count = #{luggageCount}, special_requests = #{specialRequests}, updated_at = #{updatedAt},
where passenger_id = #{passengerId}

mapper.insert:
insert into booking_passenger_relation(
    booking_id, passenger_id, is_primary, ticket_number, seat_number, luggage_tags, check_in_status
) values(
    #{bookingId}, #{passengerId}, #{isPrimary}, #{ticketNumber}, #{seatNumber}, #{luggageTags}, #{checkInStatus}
)

mapper.update:
update booking_passenger_relation
is_primary = #{isPrimary}, ticket_number = #{ticketNumber}, seat_number = #{seatNumber}, luggage_tags = #{luggageTags}, check_in_status = #{checkInStatus},
where booking_id = #{bookingId} and passenger_id = #{passengerId}
