SELECT ss.id, ss.session_no, ss.user_id, ss.employee_id, ss.session_status, ss.session_type, ss.subject, ss.start_time, ss.end_time, ss.service_duration, ss.wait_duration, ss.user_rating, ss.user_comment, ss.service_remark, ss.create_time, ss.update_time, u.username as user_name, u.first_name as user_avatar, e.name as service_name FROM service_session ss LEFT JOIN users u ON ss.user_id = u.user_id LEFT JOIN employees e ON ss.employee_id = e.id WHERE ss.employee_id = #{employeeId}
AND ss.session_status = #{status}
AND DATE(ss.create_time) >= #{startDate}
AND DATE(ss.create_time) <= #{endDate}
AND (ss.subject LIKE CONCAT('%', #{keyword}, '%') OR u.username LIKE CONCAT('%', #{keyword}, '%') OR ss.session_no LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY ss.create_time DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*)
FROM service_session ss
LEFT JOIN users u ON ss.user_id = u.user_id
WHERE ss.employee_id = #{employeeId}

    AND ss.session_status = #{status}


    AND DATE(ss.create_time) >= #{startDate}


    AND DATE(ss.create_time) <= #{endDate}


    AND (ss.subject LIKE CONCAT('%', #{keyword}, '%') 
         OR u.username LIKE CONCAT('%', #{keyword}, '%')
         OR ss.session_no LIKE CONCAT('%', #{keyword}, '%'))

SELECT ss.id, ss.session_no, ss.user_id, ss.employee_id, ss.session_status, ss.session_type, ss.subject, ss.start_time, ss.end_time, ss.service_duration, ss.wait_duration, ss.user_rating, ss.user_comment, ss.service_remark, ss.create_time, ss.update_time, u.username as user_name, u.first_name as user_avatar, e.name as service_name FROM service_session ss LEFT JOIN users u ON ss.user_id = u.user_id LEFT JOIN employees e ON ss.employee_id = e.id WHERE 1=1
AND ss.session_status = #{status}
AND DATE(ss.create_time) >= #{startDate}
AND DATE(ss.create_time) <= #{endDate}
AND (ss.subject LIKE CONCAT('%', #{keyword}, '%') OR u.username LIKE CONCAT('%', #{keyword}, '%') OR ss.session_no LIKE CONCAT('%', #{keyword}, '%') OR e.name LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY ss.create_time DESC LIMIT #{offset}, #{limit}

SELECT COUNT(*) FROM service_session ss LEFT JOIN users u ON ss.user_id = u.user_id LEFT JOIN employees e ON ss.employee_id = e.id WHERE 1=1
AND ss.session_status = #{status}
AND DATE(ss.create_time) >= #{startDate}
AND DATE(ss.create_time) <= #{endDate}
AND (ss.subject LIKE CONCAT('%', #{keyword}, '%') OR u.username LIKE CONCAT('%', #{keyword}, '%') OR ss.session_no LIKE CONCAT('%', #{keyword}, '%') OR e.name LIKE CONCAT('%', #{keyword}, '%'))
