mapper.select:
SELECT tgva.id, tgva.assignment_date, tgva.destination, tgva.total_people, tgva.adult_count, tgva.child_count, tgva.contact_phone, tgva.contact_person, tgva.pickup_method, tgva.pickup_location, tgva.dropoff_location, tgva.remarks, tgva.next_destination, tgva.status, tgva.booking_ids, tgva.tour_schedule_order_ids, tgva.passenger_details, tgva.special_requirements, tgva.dietary_restrictions, tgva.luggage_info, tgva.emergency_contact, tgva.language_preference, tgva.created_time, tgva.updated_time, tgva.guide_id, tgva.guide_name, g.phone as guide_phone, g.email as guide_email, g.languages as guide_languages, g.experience_years as guide_experience, tgva.vehicle_id, tgva.license_plate, tgva.vehicle_type, v.seat_count, v.location as vehicle_location, v.notes as vehicle_notes FROM tour_guide_vehicle_assignment tgva LEFT JOIN guides g ON tgva.guide_id = g.guide_id LEFT JOIN vehicles v ON tgva.vehicle_id = v.vehicle_id
AND tgva.assignment_date >= #{startDate}
AND tgva.assignment_date <= #{endDate}
AND tgva.destination LIKE CONCAT('%', #{destination}, '%')
AND tgva.guide_name LIKE CONCAT('%', #{guideName}, '%')
AND tgva.license_plate LIKE CONCAT('%', #{licensePlate}, '%')
AND tgva.status = #{status}
ORDER BY tgva.assignment_date DESC, tgva.created_time DESC

SELECT tgva.id, tgva.assignment_date, tgva.destination, tgva.total_people, tgva.adult_count, tgva.child_count, tgva.contact_phone, tgva.contact_person, tgva.pickup_method, tgva.pickup_location, tgva.dropoff_location, tgva.remarks, tgva.next_destination, tgva.status, tgva.booking_ids, tgva.tour_schedule_order_ids, tgva.passenger_details, tgva.special_requirements, tgva.dietary_restrictions, tgva.luggage_info, tgva.emergency_contact, tgva.language_preference, tgva.created_time, tgva.updated_time, tgva.guide_id, tgva.guide_name, NULL as guide_phone, NULL as guide_email, NULL as guide_languages, NULL as guide_experience, tgva.vehicle_id, tgva.license_plate, tgva.vehicle_type, COALESCE(v.seat_count, 25) as seat_count, COALESCE(v.location, v.current_location, '未知位置') as vehicle_location, v.notes as vehicle_notes FROM tour_guide_vehicle_assignment tgva LEFT JOIN vehicles v ON tgva.vehicle_id = v.vehicle_id WHERE tgva.destination = #{destination}
AND tgva.assignment_date = #{assignmentDate}
ORDER BY tgva.created_time

SELECT tgva.id, tgva.assignment_date, tgva.destination, tgva.total_people, tgva.adult_count, tgva.child_count, tgva.contact_phone, tgva.contact_person, tgva.pickup_method, tgva.pickup_location, tgva.dropoff_location, tgva.remarks, tgva.next_destination, tgva.status, tgva.booking_ids, tgva.tour_schedule_order_ids, tgva.passenger_details, tgva.special_requirements, tgva.dietary_restrictions, tgva.luggage_info, tgva.emergency_contact, tgva.language_preference, tgva.created_time, tgva.updated_time, tgva.guide_id, tgva.guide_name, NULL as guide_phone, NULL as guide_email, NULL as guide_languages, NULL as guide_experience, tgva.vehicle_id, tgva.license_plate, tgva.vehicle_type, COALESCE(v.seat_count, 25) as seat_count, COALESCE(v.location, v.current_location, '未知位置') as vehicle_location, v.notes as vehicle_notes, -- 获取关联的真实订单号（通过booking_id匹配） ( SELECT GROUP_CONCAT(DISTINCT tso.order_number SEPARATOR ',') FROM tour_schedule_order tso WHERE FIND_IN_SET(tso.booking_id, REPLACE(REPLACE(REPLACE(tgva.tour_schedule_order_ids, '[', ''), ']', ''), ' ', '')) AND tso.tour_date = tgva.assignment_date LIMIT 1 ) as real_order_numbers FROM tour_guide_vehicle_assignment tgva LEFT JOIN vehicles v ON tgva.vehicle_id = v.vehicle_id WHERE ( tgva.destination = #{location} OR tgva.destination LIKE CONCAT('%', #{location}, '%') OR ( #{location} = '酒(徒步)' AND tgva.destination LIKE '%亚瑟港%' ) OR ( #{location} = '布' AND tgva.destination LIKE '%布鲁尼%' ) OR ( #{location} = '霍' AND tgva.destination LIKE '%霍巴特%' ) OR ( #{location} = '亚' AND tgva.destination LIKE '%亚瑟港%' ) )
AND tgva.assignment_date = #{assignmentDate}
ORDER BY tgva.created_time

SELECT tgva.id, tgva.assignment_date, tgva.destination, tgva.total_people, tgva.adult_count, tgva.child_count, tgva.contact_phone, tgva.contact_person, tgva.pickup_method, tgva.pickup_location, tgva.dropoff_location, tgva.remarks, tgva.next_destination, tgva.status, tgva.booking_ids, tgva.tour_schedule_order_ids, tgva.passenger_details, tgva.special_requirements, tgva.dietary_restrictions, tgva.luggage_info, tgva.emergency_contact, tgva.language_preference, tgva.created_time, tgva.updated_time, tgva.guide_id, tgva.guide_name, g.phone as guide_phone, g.email as guide_email, g.languages as guide_languages, g.experience_years as guide_experience, tgva.vehicle_id, tgva.license_plate, tgva.vehicle_type, v.seat_count, v.location as vehicle_location, v.notes as vehicle_notes FROM tour_guide_vehicle_assignment tgva LEFT JOIN guides g ON tgva.guide_id = g.guide_id LEFT JOIN vehicles v ON tgva.vehicle_id = v.vehicle_id WHERE tgva.guide_id = #{guideId}
AND tgva.assignment_date = #{assignmentDate}
ORDER BY tgva.assignment_date DESC, tgva.created_time

SELECT tgva.id, tgva.assignment_date, tgva.destination, tgva.total_people, tgva.adult_count, tgva.child_count, tgva.contact_phone, tgva.contact_person, tgva.pickup_method, tgva.pickup_location, tgva.dropoff_location, tgva.remarks, tgva.next_destination, tgva.status, tgva.booking_ids, tgva.tour_schedule_order_ids, tgva.passenger_details, tgva.special_requirements, tgva.dietary_restrictions, tgva.luggage_info, tgva.emergency_contact, tgva.language_preference, tgva.created_time, tgva.updated_time, tgva.guide_id, tgva.guide_name, g.phone as guide_phone, g.email as guide_email, g.languages as guide_languages, g.experience_years as guide_experience, tgva.vehicle_id, tgva.license_plate, tgva.vehicle_type, v.seat_count, v.location as vehicle_location, v.notes as vehicle_notes FROM tour_guide_vehicle_assignment tgva LEFT JOIN guides g ON tgva.guide_id = g.guide_id LEFT JOIN vehicles v ON tgva.vehicle_id = v.vehicle_id WHERE tgva.vehicle_id = #{vehicleId}
AND tgva.assignment_date = #{assignmentDate}
ORDER BY tgva.assignment_date DESC, tgva.created_time

mapper.update:
UPDATE tour_guide_vehicle_assignment
assignment_date = #{assignmentDate}, destination = #{destination}, guide_id = #{guideId}, guide_name = #{guideName}, vehicle_id = #{vehicleId}, license_plate = #{licensePlate}, vehicle_type = #{vehicleType}, total_people = #{totalPeople}, adult_count = #{adultCount}, child_count = #{childCount}, contact_phone = #{contactPhone}, contact_person = #{contactPerson}, pickup_method = #{pickupMethod}, pickup_location = #{pickupLocation}, dropoff_location = #{dropoffLocation}, remarks = #{remarks}, next_destination = #{nextDestination}, status = #{status}, booking_ids = #{bookingIds}, tour_schedule_order_ids = #{tourScheduleOrderIds}, passenger_details = #{passengerDetails}, special_requirements = #{specialRequirements}, dietary_restrictions = #{dietaryRestrictions}, luggage_info = #{luggageInfo}, emergency_contact = #{emergencyContact}, language_preference = #{languagePreference}, updated_time = #{updatedTime}, updated_by = #{updatedBy}
WHERE id = #{id}
