mapper.update:
UPDATE tour_schedule_order t1
SET t1.dropoff_location = #{flightNumber}, t1.updated_at = NOW()
WHERE t1.booking_id = #{bookingId} 
AND t1.day_number = (
    SELECT max_day FROM (
        SELECT MAX(day_number) as max_day 
        FROM tour_schedule_order t2 
        WHERE t2.booking_id = #{bookingId}
    ) as temp
)

UPDATE tour_schedule_order t1
SET t1.dropoff_location = #{dropoffLocation}, t1.updated_at = NOW()
WHERE t1.booking_id = #{bookingId} 
AND t1.day_number = (
    SELECT max_day FROM (
        SELECT MAX(day_number) as max_day 
        FROM tour_schedule_order t2 
        WHERE t2.booking_id = #{bookingId}
    ) as temp
)

mapper.select:
SELECT tso.*, tgva.guide_name, tgva.destination as tour_destination, CONCAT( IFNULL(tgva.license_plate, ''), CASE WHEN tgva.license_plate IS NOT NULL AND tgva.vehicle_type IS NOT NULL THEN ' - ' ELSE '' END, IFNULL(tgva.vehicle_type, '') ) as vehicle_info FROM tour_schedule_order tso LEFT JOIN tour_guide_vehicle_assignment tgva ON tso.tour_date = tgva.assignment_date AND ( -- 🔧 修复：使用 tour_schedule_order_ids 正确关联，避免笛卡尔积 tgva.tour_schedule_order_ids IS NOT NULL AND JSON_CONTAINS(tgva.tour_schedule_order_ids, CAST(tso.booking_id AS JSON), '$') ) -- 🚌 排除酒店摆渡任务的分配（摆渡是额外任务，不应显示在酒店送客表中） AND (tgva.destination IS NULL OR tgva.destination NOT LIKE '%酒店摆渡%') WHERE tso.tour_date = #{tourDate} AND tso.dropoff_location LIKE CONCAT('%', #{hotelName}, '%')
AND tgva.guide_name = #{guideName}
ORDER BY tgva.guide_name ASC, tso.order_number ASC

