mapper.update:
UPDATE tour_schedule_order t1
SET t1.dropoff_location = #{flightNumber}, t1.updated_at = NOW()
WHERE t1.booking_id = #{bookingId} 
AND t1.day_number = (
    SELECT max_day FROM (
        SELECT MAX(day_number) as max_day 
        FROM tour_schedule_order t2 
        WHERE t2.booking_id = #{bookingId}
    ) as temp
)

UPDATE tour_schedule_order t1
SET t1.dropoff_location = #{dropoffLocation}, t1.updated_at = NOW()
WHERE t1.booking_id = #{bookingId} 
AND t1.day_number = (
    SELECT max_day FROM (
        SELECT MAX(day_number) as max_day 
        FROM tour_schedule_order t2 
        WHERE t2.booking_id = #{bookingId}
    ) as temp
)

mapper.select:
SELECT tso.*, tgva.guide_name, tgva.destination as tour_destination, CONCAT( IFNULL(tgva.license_plate, ''), CASE WHEN tgva.license_plate IS NOT NULL AND tgva.vehicle_type IS NOT NULL THEN ' - ' ELSE '' END, IFNULL(tgva.vehicle_type, '') ) as vehicle_info FROM tour_schedule_order tso LEFT JOIN tour_guide_vehicle_assignment tgva ON tso.tour_date = tgva.assignment_date AND ( -- üîß ‰øÆÂ§çÔºö‰ΩøÁî® tour_schedule_order_ids Ê≠£Á°ÆÂÖ≥ËÅîÔºåÈÅøÂÖçÁ¨õÂç°Â∞îÁßØ tgva.tour_schedule_order_ids IS NOT NULL AND JSON_CONTAINS(tgva.tour_schedule_order_ids, CAST(tso.booking_id AS JSON), '$') ) -- üöå ÊéíÈô§ÈÖíÂ∫óÊëÜÊ∏°‰ªªÂä°ÁöÑÂàÜÈÖçÔºàÊëÜÊ∏°ÊòØÈ¢ùÂ§ñ‰ªªÂä°Ôºå‰∏çÂ∫îÊòæÁ§∫Âú®ÈÖíÂ∫óÈÄÅÂÆ¢Ë°®‰∏≠Ôºâ AND (tgva.destination IS NULL OR tgva.destination NOT LIKE '%ÈÖíÂ∫óÊëÜÊ∏°%') WHERE tso.tour_date = #{tourDate} AND tso.dropoff_location LIKE CONCAT('%', #{hotelName}, '%')
AND tgva.guide_name = #{guideName}
ORDER BY tgva.guide_name ASC, tso.order_number ASC

