mapper.update:
UPDATE tour_schedule_order t1
SET t1.dropoff_location = #{flightNumber}, t1.updated_at = NOW()
WHERE t1.booking_id = #{bookingId} 
AND t1.day_number = (
    SELECT max_day FROM (
        SELECT MAX(day_number) as max_day 
        FROM tour_schedule_order t2 
        WHERE t2.booking_id = #{bookingId}
    ) as temp
)

UPDATE tour_schedule_order t1
SET t1.dropoff_location = #{dropoffLocation}, t1.updated_at = NOW()
WHERE t1.booking_id = #{bookingId} 
AND t1.day_number = (
    SELECT max_day FROM (
        SELECT MAX(day_number) as max_day 
        FROM tour_schedule_order t2 
        WHERE t2.booking_id = #{bookingId}
    ) as temp
)

mapper.select:
SELECT tso.*, tgva.guide_name, CONCAT( IFNULL(tgva.license_plate, ''), CASE WHEN tgva.license_plate IS NOT NULL AND tgva.vehicle_type IS NOT NULL THEN ' - ' ELSE '' END, IFNULL(tgva.vehicle_type, '') ) as vehicle_info FROM tour_schedule_order tso LEFT JOIN tour_guide_vehicle_assignment tgva ON tso.tour_date = tgva.assignment_date WHERE tso.tour_date = #{tourDate} AND tso.dropoff_location LIKE CONCAT('%', #{hotelName}, '%')
AND tgva.guide_name = #{guideName}
ORDER BY tgva.guide_name ASC, tso.order_number ASC

